function SVGAnimFrames(elm,tobefound,repeat,frametime,delay){var counter=0;var detectFrame=parseInt(document.querySelectorAll(elm+' '+tobefound).length);var totalFrames=parseInt(document.querySelectorAll(elm+' '+tobefound).length);function killAnim(){counter=0;detectFrame=0;clearInterval(window.intervalID)}function restartSVGAnim(){killAnim();intervalID=setInterval(animateSVGFrames,frametime)}function animateSVGFrames(){var detectFrame=counter++;for(var i=0;i<document.querySelectorAll(elm+" > g > g *").length;i+=1){document.querySelectorAll(elm+" *")[i].removeAttribute("vector-effect")}for(var i=0;i<totalFrames;i+=1){if(counter>totalFrames){return false}document.querySelectorAll(elm+' '+tobefound)[i].style.display="none";document.querySelectorAll(elm+' '+tobefound)[detectFrame].style.display="block"}if(repeat==="no-repeat"){if(counter===totalFrames){for(var i=0;i<totalFrames;i+=1){if(counter>totalFrames){clearInterval(window.intervalID);counter=0;var detectFrame=totalFrames;return false}document.querySelectorAll(elm+' '+tobefound)[i].style.display="none";document.querySelectorAll(elm+' '+tobefound)[detectFrame].style.display="block"}}}else{if(counter===totalFrames){setTimeout(function(){restartSVGAnim()},delay)}else if(detectFrame>=totalFrames){setTimeout(function(){restartSVGAnim()},delay)}}}window.intervalID=setInterval(animateSVGFrames,frametime);return false};